import React from 'react';
import './App.css';
import ContentPage from './ContentPage';
import ReactGA from 'react-ga';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

var mod;

const CHAPTERS = [4, 3]
const APIS = ["https://api.sheety.co/3664fce7-19ab-434d-865c-fbe5034cdfa1",
"https://api.sheety.co/8a980111-20a6-4c83-bc7a-596335e640c2"]

export class Charts extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      sorted: [],
      headers: [],
      loaded: false,
      info: false,
      mod: '',
    }
  }

  organiseData() {
    if (this.state.data) {
      for (let i = 1; i < CHAPTERS[this.props.active_week-1]; i++) {
        let segments = this.state.data.filter(item => item.chapter == i.toString());
        let header = this.state.data.filter(item => item.chapter == 'C'+i.toString())
        this.state.headers.push(header);
        this.state.sorted.push(segments)
      }
      this.setState({loaded: true});
    }
  }

  componentDidMount() {
    let API = APIS[this.props.active_week-1];
    ReactGA.pageview(window.location.pathname + window.location.search);

    fetch(API)
      .then((response) => {
        return response.json()
      }).then((json) => {
        this.setState({data: json});
        this.organiseData()
      })
  }

  getInfo(e, code) {
    // e.stopPropagation();
    // this.setState({mod: code})
    mod = code
    console.log(mod);
    this.forceUpdate()
  }


  renderData() {
    // console.log(this.state.sorted)
    return this.state.info ? '' : this.state.sorted.map((chapter, index) =>
        <div key={index} className="chapter-container" id={chapter[0].chapter}>
          <div className="chapter-header">{this.state.headers[index][0].title}</div>
          {chapter.map((mods, index) => 
            <div key={index} className="module-container">
            {console.log('info is' + chapter[index])}
            <Link to ={`/${mods.code}`} id={index} onClick={e => this.getInfo(e, mods.code)}><img className="card-img" src={mods.picture}></img></Link>            
            {/* <Link to ={`/${mods.code}`} id={index} onClick={() => {console.log('recorded click', this)}}><img className="card-img" src={mods.picture}></img></Link>             */}
            {/* <Route path={`/${mods.code}`} component={() => <ContentPage data={this.state.data} path={this.state.info}/>} /> */}
            </div>
          )}
        </div>
    )
  }

  render() {
    return(
      <div>
          {/* {this.state.loaded ? this.renderData() : ''} */}
          {this.renderData()}
          {console.log('renderer getting info', mod)}
          {/* <Route path={`/${this.state.mod}`} component={ContentPage} /> */}
          {mod ? <Route path={`/${mod}`} component={() => <ContentPage data={this.state.sorted} path={mod}/> } /> : ''}

          {/* <Route path={`/${this.state.mod}`} render={this.renderInfo}/> */}
      </div>
    )
  }
}

class RenderInfo extends React.Component {
  constructor(props) {
    super(props)
  }
  componentDidMount() {
    console.log(this.props.code);
    {this.props.context.setState({info: this.props.data})}
  }

  render() {
    return (
      <div>
        <ContentPage data={this.props.info} />
      </div>
    )
  }
}

export default Charts;
