import React from 'react';

export class Charts extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      sorted: []
    }
  }

  componentDidMount() {
    fetch("https://sheetdb.io/api/v1/hse3ttfafjgip")
      .then((response) => {
        return response.json()
      }).then((json) => {
        this.setState({data: json});
        console.log('hi');

        let final = [];
        for (let i = 1; i < 5; i++) {
          let segments = this.state.data.filter(item => item.Week == i.toString());
          if (segments.length> 0) {final.push(segments);}
          console.log(segments)
        }
        this.setState({sorted: final});
        console.log(this.state.sorted);
      });
  }


  renderData() {
    this.state.sorted.map((week) => 
      <div key={week}>
        {week.map((row) => 
          <div key={row.Module}>
            <p>{row.Module}</p>
            <p>{row.Instructions}</p>
          </div>
        )}
      </div>
    )
  }

  // renderData() {
  //     this.state.data.map((row) => 
  //     <div key={row.Module}>
  //       <p>{row.Module}</p>
  //       <p>{row.Instructions}</p>
  //     </div>
  //     )

  // }

  render() {
    return(
      <div>
        <h2>{'Loading'}</h2>
        {this.state.sorted.length > 0 && this.renderData()}
      </div>
    )
  }
}
